import React, { Component } from "react";
import { MDBBtn, MDBCol, MDBContainer } from "mdbreact";
import "./index.css";
import TaskList from "./TaskList";

class App extends Component {
  state = {
    tasks: []
  };
  handleAdd = () => {
    const newTask = {
      name: "Task number",
      number: this.state.tasks.length + 1
    };
    const newTasks = [...this.state.tasks, newTask];
    this.setState({
      tasks: newTasks
    });
  };
  handleRemove = () => {
    if (this.state.tasks.length > 0) {
      let tasksCopy = this.state.tasks;
      tasksCopy.pop();
      this.setState({
        tasks: tasksCopy
      });
    }
  };
  handleClear = () => {
    this.setState({
      tasks: []
    });
  };
  render() {
    return (
      <MDBContainer>
        <MDBCol className="text-center">
          <h1>TODO App</h1>
          <MDBBtn onClick={this.handleAdd} target="blank" color="light-blue">
            <strong>ADD</strong>
          </MDBBtn>
          <MDBBtn onClick={this.handleRemove} target="blank" color="light-blue">
            <strong>Remove</strong>
          </MDBBtn>
          <MDBBtn onClick={this.handleClear} target="blank" color="light-blue">
            <strong>Clear</strong>
          </MDBBtn>
        </MDBCol>
        <TaskList {...this.state} />
      </MDBContainer>
    );
  }
}

export default App;
